mapper.select:
SELECT * FROM team_db.attendance
WHERE empno = #{empno}
AND date = date_format(now(), '%Y%m%d')

mapper.update:
UPDATE attendance
SET
chkin = TIME(NOW()),
attend_note = #{note}
WHERE
empno = #{empno}
AND date = DATE(NOW());

UPDATE attendance
SET
chkout = TIME(NOW())

WHERE
empno = #{empno}
AND date = DATE(NOW());
